package jssi.ursa.credential;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertNotNull;

class CredentialPrimaryPublicKeyTest {

    @Test
    void deserialize_test() {

        String data = "{" +
                "\"n\":\"\"," +
                "\"s\":\"\"," +
                "\"r\":{" +
                        "\"age\":\"\"," +
                        "\"height\":\"\"," +
                        "\"name\":\"\"," +
                        "\"sex\":\"\"," +
                        "\"master_secret\": \"\"" +
                "}," +
                "\"rctxt\":\"\"," +
                "\"z\":\"\"" +
                "}";


        CredentialPrimaryPublicKey primaryPublicKey = null;
        try {
            ObjectMapper objectMapper = new ObjectMapper();
            primaryPublicKey = objectMapper.readValue(data, CredentialPrimaryPublicKey.class);
        } catch (JsonProcessingException e){}
        assertNotNull(primaryPublicKey);
    }
}